const WEIGHTS=[500,700,900];const MAX_ROTATE=20;const MAX_SCALE=1;const MIN_SCALE=.5;const IS_RANSOM=false;const el=document.querySelector(".sketchy");const text=el.innerText;const spans=[];const convertToSpans=()=>{const t=text.split(" ");el.innerHTML="";t.forEach(t=>{const n=t.split("").filter(t=>!!t.trim().length);const e=document.createElement("span");n.forEach(t=>{const n=document.createElement("span");n.className="letter";n.innerText=t;e.appendChild(n);spans.push(n)});el.appendChild(e)})};const sketchify=()=>{const t=MAX_SCALE-MIN_SCALE;spans.forEach(n=>{const e=MAX_ROTATE*(Math.random()-Math.random());const s=MIN_SCALE+t*Math.random();if(IS_RANSOM){n.style.fontWeight=WEIGHTS[Math.floor(WEIGHTS.length*Math.random())]}n.style.transform=`rotate(${e}deg) scale(${s})`})};convertToSpans();sketchify();